#include "Robot.h"
#include <math.h>
#include <Utility.h>
#include "TimedDrive.h"

#ifndef M_PI
#define	M_PI	3.1415926535
#endif

TimedDrive::TimedDrive( float seconds )
{
    // Use Requires() here to declare subsystem dependencies
    // eg. Requires(chassis);
    //Requires(Robot::RobotDrive3());

    m_cycleTime = seconds;
    m_startTime = 0.0;
}

// Called just before this Command runs the first time
void TimedDrive::Initialize()
{
    m_startTime = Timer::GetFPGATimestamp();
    Robot::RobotDrive3()->Set(0.0);
}

// Called repeatedly when this Command is scheduled to run
void TimedDrive::Execute()
{
    double elapsed = Timer::GetFPGATimestamp() - m_startTime;
    // sin ranges from -1.0 to +1.0, we need 0.0 to +1.0
    float breathe = (sin(2.0 * M_PI * elapsed / m_cycleTime) + 1.0) / 2.0;
    Robot::DriveCommand()->Set(breathe);
}

// Make this return true when this Command no longer needs to run execute()
bool TimedDrive::IsFinished()
{
    return false;
}

// Called once after isFinished returns true
void TimedDrive::End()
{
    ;
}

// Called when another command which requires one or more of the same
// subsystems is scheduled to run
void TimedDrive::Interrupted()
{
    Robot::DriveCommand()->Set(0.0);
}
